INSERT INTO `sevenmultiptday` VALUES ('58', '1435331', 'http://xueqiu.com/5124430882/153301132', '1594113521000', '【蓬佩奥：美国正“考虑”禁止TikTok】据环球时报，美国有线电视新闻网(CNN) 7月7日报道，美国国务卿蓬佩奥周一晚间表示，美国正在“考虑”禁止包括TikTok在内的中国社交媒体应用程序(APP)。他在接受福克斯新闻网记者劳拉·英格拉汉姆采访时，暗示美国政府可能采取这一举动，并表示“我们在非常认真地对待此事。”', '14', '1');
