INSERT INTO `sevenmultiptday` VALUES ('60', '1435329', 'http://xueqiu.com/5124430882/153301092', '1594113488000', '【华泰回应App疑似崩溃：近期行情火爆 账户资产查询量激增】华泰证券交易软件手机客户端疑似崩溃，华泰证券回应称，近期行情火爆，今天下午涨乐财富通APP账户资产查询量激增，部分客户通过“我的”页面查询资产刷新出现延迟。期间，客户可通过“交易”页面查询账户资产，交易、行情、登录等功能未受影响。目前，“我的”页面资产展示已恢复。', '6', '1');
