INSERT INTO `sevenmultiptday` VALUES ('62', '1435322', 'http://xueqiu.com/5124430882/153301047', '1594113456000', '【万丰奥威：控股股东万丰集团与宝钢金属有限公司终止战略合作】万丰奥威公告，公司接到控股股东万丰集团的通知，万丰集团与宝钢金属有限公司近日签署了《战略合作终止备忘录》。受新型冠状病毒肺炎疫情客观因素影响，以及公司进军通用航空先进飞机制造业新蓝海，产业规划布局发生了变化，双方未能就战略合作事宜进一步落实，经友好协商，决定终止本次战略合作。', '0', '0');
