INSERT INTO `sevenmultiptday` VALUES ('71', '1435295', 'http://xueqiu.com/5124430882/153300524', '1594113086000', '【美团成立优选事业部入局社区团购，小象更名买菜事业部】美团今日发布组织调整公告称，为进一步探索社区生鲜零售业态，满足差异化消费需求，推动生鲜零售线上线下加速融合，将成立“优选事业部”，进入社区团购赛道，由美团高级副总裁、S-team成员陈亮负责。同时，原小象事业部更名为“买菜事业部”，继续加速发展美团买菜业务，负责人为辛崇阳，向陈亮汇报。', '0', '0');
