INSERT INTO `sevenmultiptday` VALUES ('95', '1435211', 'http://xueqiu.com/5124430882/153297469', '1594111184000', '【《中央广播电视总台5G媒体应用白皮书（2020版）》终审会召开】中央广播电视总台召开《中央广播电视总台5G媒体应用白皮书（2020版）》终审会。5G白皮书总结了中央广播电视总台基于5G技术，面向4K超高清、VR和移动生产场景的研究和实践成果，是媒体行业首次对于5G技术应用提出的技术规范，为5G技术与媒体生产的深度融合和快速发展打下坚实的基础。', '4', '0');
