INSERT INTO `sevenmultiptday` VALUES ('105', '1435808', 'http://xueqiu.com/5124430882/153318175', '1594127386000', '【达美航空客机遭鸟击在肯尼迪国际机场迫降】据美国全国广播公司《今日》节目报道，当地时间周一晚上，一架从美国佛罗里达州棕榈滩飞往纽约拉瓜迪亚机场的达美航空飞机空客A319飞行途中遭遇鸟击后，紧急改道在肯尼迪国际机场迫降，无人受伤。（中国日报网）', '0', '0');
