INSERT INTO `sevenmultiptday` VALUES ('205', '1436185', 'http://xueqiu.com/5124430882/153335463', '1594141054000', '【亚洲债券投资热情将消退】在新冠疫情毫无减退迹象，资产变得越来越贵的情况下，M&G Investments对亚洲债券采取了防守姿态。固定收益投资总监Pierre Chartres表示，证券投资经理决定减持当地债券，但仍然看好印尼和新加坡的债券。鉴于新兴市场高收益或投资级公司债等已经收复了70%至90%的失地，目前买入机会很少。', '0', '0');
