INSERT INTO `sevenmultiptday` VALUES ('220', '1436877', 'http://xueqiu.com/5124430882/153368595', '1594177267000', '【6月份全国铁路发送旅客1.66亿人次 环比增长9.4%】记者从中国国家铁路集团有限公司获悉，今年上半年，全国铁路发送旅客8.18亿人次，受疫情影响，同比下降53.9%。3月份以来，铁路客流逐步回升，6月份全国铁路发送旅客1.66亿人次，日均发送旅客553万人次，环比增加47万人次，增长9.4%。（央视新闻）', '1', '0');
INSERT INTO `sevenmultiptday` VALUES ('221', '1436875', 'http://xueqiu.com/5124430882/153368334', '1594177181000', '【达安基因：公司新型冠状病毒核酸检测相关产品已经发往了全球140多个国家和地区】达安基因在互动平台表示，截至目前，公司新型冠状病毒核酸检测相关产品已经发往了全球140多个国家和地区，出口的比重比内销小。', '1', '0');
