INSERT INTO `sevenmultiptday` VALUES ('235', '1436837', 'http://xueqiu.com/5124430882/153364432', '1594175840000', '【中关村再降企业创新成本 购买科技服务最高补贴50万元】中关村科学城管委会透露，2020年北京市海淀区协同创新券近日启动申报，对企业通过平台购买科技服务的，海淀区按实际成交额给予购买费用最高30%的补贴，最高补贴金额50万元。据悉，该项补贴将用于补贴企业通过平台向服务机构购买的科技服务费用，包括研发设计服务、分析检测服务、实验试制服务、创新支撑服务等。', '0', '0');
