INSERT INTO `sevenmultiptday` VALUES ('242', '1436816', 'http://xueqiu.com/5124430882/153362540', '1594175155000', '【湖北黄梅突发山体滑坡9人被埋】受特大暴雨影响，7月8日凌晨4时许，黄梅县大河镇袁山村3组突发山体滑坡，导致5户9名群众被埋。目前该村其他40多名村民已转移到安全地带。对被埋群众的抢救正在进行中。（人民日报）', '2', '0');
INSERT INTO `sevenmultiptday` VALUES ('243', '1436815', 'http://xueqiu.com/5124430882/153362494', '1594175143000', '【Allstate同意以约40亿美元收购National General】根据公司声明，Allstate已达成协议以每股34.50美元收购National General，总额约40亿美元。交易预计将于2021年初完成。National General的股东将从Allstate获得每股32美元现金，还将获得每股2.50美元的股息。', '0', '0');
INSERT INTO `sevenmultiptday` VALUES ('244', '1436812', 'http://xueqiu.com/5124430882/153362118', '1594175008000', '【金时科技：主营业务是烟标的研发 会积极关注电子烟这一新型产品的发展态势】金时科技在互动平台表示，公司目前主营业务是烟标的研发，生产及销售。公司也会积极关注电子烟这一新型产品的发展态势。', '1', '0');
