INSERT INTO `sevenmultiptday` VALUES ('350', '1437633', 'http://xueqiu.com/5124430882/153417424', '1594201008000', '【奥赛康：子公司创新药ASKB589获得临床试验批准通知书】奥赛康公告，公司及其全资子公司奥赛康生物于近日收到国家药品监督管理局下发的ASKB589注射液《药物临床试验批准通知书》。ASKB589 注射液为江苏奥赛康自主研发、具有自主知识产权的抗肿瘤生物新药，其主要通过ADCC和CDC杀伤肿瘤细胞，拟用于胃及胃食管结合部腺癌、胰腺癌等适应症。', '0', '0');
