INSERT INTO `sevenmultiptday` VALUES ('421', '1438907', 'http://xueqiu.com/5124430882/153480134', '1594260971000', '【全国“扫黄打非”办：严格整顿网络色情低俗问题 优酷、爱奇艺等被处罚】全国“扫黄打非”办9日称，“净网2020”行动推进有力阶段性成效明显，有的放矢严格整顿网络色情低俗问题。依法处罚了一批网络平台。对优酷、爱奇艺、大学生联盟网、“喵播”等网站传播低俗信息行为予以了行政处罚。针对虎牙、斗鱼等平台存在传播低俗庸俗内容、主体责任履行不到位等问题，广东、湖北等地网信部门会同“扫黄打非”办公室对相关企业进行了调查约谈，要求停止主要频道内容更新、限期整改。', '0', '0');
