INSERT INTO `sevenmultiptday` VALUES ('427', '1438890', 'http://xueqiu.com/5124430882/153477926', '1594260364000', '【纽约四分之一公寓租客疫情以来没钱付房租】据代表纽约出租物业业主的行业组织CHIP统计，纽约市超过25%的公寓租客自3月份疫情开始以来就没有支付过房租。根据纽约州的《房客安全港法案》，租户由于疫情面临经济困难时不能被驱逐。然而，房东可以继续要求租金，这意味着租客的债务可能会继续增长。', '0', '0');
