INSERT INTO `sevenmultiptday` VALUES ('429', '1438886', 'http://xueqiu.com/5124430882/153477815', '1594260333000', '【外媒：博索纳罗把拯救巴西和自己的希望都寄托在特朗普推荐的“神药”上了】“我相信羟氯喹，你们呢？”刚刚确诊感染新冠肺炎的巴西总统博索纳罗7日透露，自己正在服用被特朗普推崇为“神药”的抗疟疾药物羟氯喹，还鼓励巴西人服用此药来预防和治疗新冠。此前，特朗普本人也服用过此药用来预防新冠。 （环球网）', '8', '0');
INSERT INTO `sevenmultiptday` VALUES ('430', '1438885', 'http://xueqiu.com/5124430882/153477405', '1594260300000', '【肖钢：以银行销售主导的客户服务体系不利于基金的良性发展】原中国证监会主席肖钢表示，从运营的生态来看，我们是以银行和第三方销售主导的客户服务体系，的确不利于基金管理公司的公平竞争和良性发展。一方面，银行系的合资公司和非银行系的合资公司在渠道资源上也有很大的差异。另一方面，销售费用体系也不尽合理。', '1', '0');
