INSERT INTO `sevenmultiptday` VALUES ('432', '1438877', 'http://xueqiu.com/5124430882/153476993', '1594260180000', '【北京石景山万达广场今日十点恢复营业】7月2日，因一名新冠肺炎病毒核酸检测阳性顾客在石景山万达广场活动，石景山区立即启动应急预案，对万达广场采取了临时封闭等疫情管控措施。近日，区卫生健康委、区疾控中心已完成相关流行病学调查、环境采样检测，对密接人员实行集中医学观察，对商场环境进行全面专业消杀、清洁。外环境采样检测结果均为阴性，密接人员和万达广场所有员工的核酸检测结果均为阴性。经过综合评估，石景山万达广场已具备开业条件，于7月9日上午十点恢复营业。', '1', '0');
