INSERT INTO `sevenmultiptday` VALUES ('474', '1441277', 'http://xueqiu.com/5124430882/153644539', '1594363952000', '【中疾控：北京病例样本检测结果较武汉参考毒株在四个位点发生突变】今天，中国疾控中心公布了“2020年6月至7月北京新型冠状病毒肺炎疫情进展（二）”，其中再次强调，根据流行病学调查及病毒基因测序结果分析，引起此次疫情的毒株为L基因型欧洲家系分支I，排除由动物病毒外溢传染人导致本次疫情的可能。同时，中国疾控中心对北京52例确诊病例样本的新冠病毒全基因组高通量序列测定结果显示，与武汉参考毒株NC_045512序列相比，所有样本在四个位点发生突变。', '0', '0');
