INSERT INTO `sevenmultiptday` VALUES ('478', '1441263', 'http://xueqiu.com/5124430882/153643323', '1594363479000', '【特斯拉空头仓位逼近200亿美元】研究机构S3 Partners数据显示，特斯拉空头仓位达199.5亿美元，逼近200亿美元。特斯拉有望成为有史以来第一只空头仓位突破200亿美元的股票。美股周四收盘，特斯拉股价上涨2.08%至1394.28美元，总市值约2586.05亿美元。', '4', '0');
