INSERT INTO `sevenmultiptday` VALUES ('482', '1441252', 'http://xueqiu.com/5124430882/153642395', '1594363152000', '【法匹拉韦临床研究未能证实有效性】据日本共同网，日本藤田医科大学（爱知县）10日宣布，在新冠病毒传染病候选治疗药物“Avigan”（法匹拉韦）的临床研究中，用药患者与非用药患者的治疗效果未出现统计学上的差异。该研究未能证实法匹拉韦的明确有效性。', '7', '0');
INSERT INTO `sevenmultiptday` VALUES ('483', '1441248', 'http://xueqiu.com/5124430882/153641784', '1594362937000', '【台积电1-6月份营收6212.96亿新台币 同比增长35.2%】台积电官网的信息显示，台积电6月份营收1208.8亿新台币，折合约41亿美元。在披露6月份的营收时，台积电也一并披露了上半年的营收。1-6月份营收6212.96亿新台币，去年同期为4597.03亿新台币，同比增长35.2%。', '1', '0');
