INSERT INTO `sevenmultiptday` VALUES ('493', '1441224', 'http://xueqiu.com/5124430882/153640024', '1594362200000', '【中石油再投产两条熔喷布生产线，已投产的自建生产线增至6条】7月9日，中石油辽阳石化熔喷无纺布扩能改造项目新建的两条熔喷布生产线全部建成投产。截至目前，中石油集团在兰州石化和辽阳石化自建并投产的熔喷布生产线已增至6条，设计总产能达3200吨/年。（中国石油报）', '3', '0');
