INSERT INTO `sevenmultiptday` VALUES ('588', '1442080', 'http://xueqiu.com/5124430882/153683390', '1594391100000', '【辉瑞、BioNTech新冠疫苗预计将在年内获批】德国生物新技术公司BioNTech首席执行官表示，该公司与美国医药巨头辉瑞(PFE.N)共同开发的新冠疫苗预计将在2020年底前通过审批。据此前报道，若安全性和有效性研究成功并且疫苗获得监管部门的批准，两家公司预计，到2020年底将生产多达1亿剂，到2021年底可能再生产超过12亿剂。（界面新闻）', '55', '1');
